import React, { useState, useEffect } from "react";
import './PacMan.css';

const CELL_SIZE = 40;

const PacMan = ({maze,setMaze}) => {
    const [position, setPosition] = useState({ x: 1, y: 1 });
    const [direction, setDirection] = useState(null);

    const handleKeyDown = (e) => {
        switch (e.key) {
            case 'ArrowUp':
                setDirection('up')
                break;
            case 'ArrowDown':
                setDirection('down')
                break;
            case 'ArrowLeft':
                setDirection('left')
                break;
            case 'ArrowRight':
                setDirection('right')
                break;
            default:
                break;
        }
    }

    const movePacMan=() => {
        let newX = position.x;
        let newY = position.y;

        switch (direction) {
            case 'up':
                newY = Math.max(0, position.y - 1);
                break;
            case 'down':
                newY = Math.min(maze.length - 1, position.y + 1);
                break;
            case 'left':
                newX = Math.max(0, position.x - 1);
                break;
            case 'right':
                newX = Math.min(maze[0].length - 1, position.x + 1);
                break;
            default:
                break;
        }
        // Checks if the new position is not a wall
        if (maze[newY][newX] != 1) {
            setPosition({ x: newX, y: newY });
            if (maze[newY][newX] === 0) {
                const newMaze = maze.map((row, rowIndex) =>
                    row.map((cell, cellIndex) => {
                        if (rowIndex === newY && cellIndex === newX) {
                            return 2;
                        }
                        return cell;
                    })
                )
                setMaze(newMaze);
            }
        }
    }

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        // reduce the value from 300 to 100 to increase the PacMan's speed 
        const interval = setInterval(movePacMan, 300);
        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            clearInterval(interval)
        };
    }, [direction, position]);

    return (
        <div 
            className="pacman"
            style={{
                top: `${position.y * CELL_SIZE}px`,
                left:`${position.x * CELL_SIZE}px`
        }}></div>
    )
}

export default PacMan;
